mapper.update:
update dish
image = #{image}, description = #{description}, category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, update_user = #{updateUser}, update_time = #{updateTime},
id = #{id}

mapper.select:
select d.*, c.name as categoryName from dish d left outer join category c on d.category_id = c.id

    and d.category_id = #{categoryId}
    and d.name like concat('%', #{name} ,'%')
    and d.status = #{status}

order by create_Time desc

select * from dish


        and category_id = #{categoryId}


        and name like concat("%",#{name},"%")


        and status = #{status}


order by create_time desc
